/**
  * @brief  The application entry point.
  * @retval int
  */
int main(void)
{
  /* USER CODE BEGIN 1 */
  /* USER CODE END 1 */

  /* MCU Configuration--------------------------------------------------------*/

  /* Reset of all peripherals, Initializes the Flash interface and the Systick. */
  HAL_Init();

  /* USER CODE BEGIN Init */

  /* USER CODE END Init */

  /* Configure the system clock */
  SystemClock_Config();

  /* USER CODE BEGIN SysInit */

  /* USER CODE END SysInit */

  /* Initialize all configured peripherals */
  MX_GPIO_Init();
  MX_DMA_Init();
  MX_I2C1_Init();
  MX_USART1_UART_Init();
  MX_RTC_Init();
  MX_FATFS_Init();
  MX_CRC_Init();
  MX_SPI1_Init();
  /* USER CODE BEGIN 2 */
  Screen_init(&u8g2);

  HAL_Delay(1000);

  GPS_init(&gps);
  configure_GPS();

  HAL_UART_Receive_DMA(&huart1, usart_rx_dma_buffer, MAX_NMEA_LEN);
  __HAL_UART_ENABLE_IT(UART1, UART_IT_IDLE); 			// enable idle line interrupt
  /* USER CODE END 2 */

  /* Infinite loop */
  /* USER CODE BEGIN WHILE */
  while (1)
  {
    /* USER CODE END WHILE */


	if(GPSupdated)
	{
		__HAL_UART_DISABLE_IT(UART1, UART_IT_IDLE);
		log_data();
		GPSupdated = 0;
		//HAL_UART_Transmit(&huart1, "logged\r\n", strlen("logged\r\n"),1000);
		__HAL_UART_ENABLE_IT(UART1, UART_IT_IDLE);
		//HAL_Delay(2000);
	}


	check_buttons();

    /* USER CODE BEGIN 3 */
  }
  /* USER CODE END 3 */
}

